import React from 'react';
import './SocieteCard.css';

const SocieteCard = ({ societe }) => {
  return (
    <div className="societe-card">
      <div className="societe-logo">
        {societe.logo ? (
          <img src={societe.logo} alt={societe.nom} />
        ) : (
          <div className="societe-logo-placeholder">{societe.nom.charAt(0)}</div>
        )}
      </div>
      <h3>{societe.nom}</h3>
      <p>{societe.description}</p>
    </div>
  );
};

export default SocieteCard;
