// components/Societes/SocietesList.js
import React, { useState, useEffect } from 'react';
import { getSocietes } from '../../services/societeService';
import './SocietesList.css';

const SocietesList = () => {
  const [societes, setSocietes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSocietes = async () => {
      try {
        console.log('Tentative de chargement des sociétés');
        const data = await getSocietes();
        console.log('Sociétés reçues:', data);
        setSocietes(data);
      } catch (error) {
        console.error('Erreur lors du chargement des sociétés:', error);
        setError('Erreur lors du chargement des sociétés');
      } finally {
        setLoading(false);
      }
    };

    fetchSocietes();
  }, []);

  if (loading) return <div>Chargement...</div>;
  if (error) return <div>{error}</div>;

  return (
    <section className="societes-section">
      <h2>Sociétés de gestion</h2>
      {societes.length === 0 ? (
        <p>Aucune société de gestion disponible.</p>
      ) : (
        <div className="societes-grid">
          {societes.map(societe => (
            <div key={societe._id} className="societe-card">
              <h3>{societe.nom}</h3>
              <p>{societe.description}</p>
            </div>
          ))}
        </div>
      )}
    </section>
  );
};

export default SocietesList;
