import React from 'react';
import './ScpiCard.css';

const ScpiCard = ({ scpi }) => {
  return (
    <div className="scpi-card">
      <div className="scpi-image">
        {scpi.image ? (
          <img src={scpi.image} alt={scpi.nom} />
        ) : (
          <div className="scpi-image-placeholder">{scpi.nom.charAt(0)}</div>
        )}
      </div>
      <h3>{scpi.nom}</h3>
      <p className="scpi-societe">{scpi.societe.nom}</p>
      <div className="scpi-details">
        <p><strong>Prix part:</strong> {scpi.prixPart} €</p>
        <p><strong>Rendement:</strong> {scpi.rendement}%</p>
        <p><strong>Capitalisation:</strong> {scpi.capitalisation} M€</p>
        <p><strong>Type:</strong> {scpi.type}</p>
      </div>
      <p className="scpi-description">{scpi.description}</p>
    </div>
  );
};

export default ScpiCard;
